import React,{useEffect} from "react";
import { fetchAsyncMoviesbyId } from "../features/movieSlice";
import { useSelector, useDispatch } from "react-redux";

const Moviedetails = () => {

  const dispatch = useDispatch()

  const fetchMovieId = useSelector((state)=>{
    return state.movies.id
  }) 

 useEffect(() => {
    dispatch(fetchAsyncMoviesbyId(fetchMovieId))
    // eslint-disable-next-line
  }, []);

  const single = useSelector((state)=>{
    return state.movies.movie
  })


  return <div className="movie">
    <img src={single.Poster} alt="" />
  </div>;
};

export default Moviedetails;
