import React,{useEffect} from "react";
import { fetchAsyncMoviesbyId } from "../features/movieSlice";
import { useSelector, useDispatch } from "react-redux";

const Moviedetails = () => {

  const dispatch = useDispatch()

  const fetchMovieId = useSelector((state)=>{
    return state.movies.id
  }) 

  const single =   useEffect(() => {
    return dispatch(fetchAsyncMoviesbyId(fetchMovieId))
    // eslint-disable-next-line
  }, []);

  console.log(single);


  return <div className="movie">moviedetails</div>;
};

export default Moviedetails;
