import React, { useCallback, useEffect } from 'react'
import instance from '../api/Api'
import { apiKey } from '../api/Apikey'
import { addMovies } from '../features/movieSlice'
import { useSelector,useDispatch } from 'react-redux'



const Movielisting = () => {
  const dispatch = useDispatch()
  const movieList = useSelector((state)=>{return state.movies.movies})
  console.log(movieList);

  const fetchMovies = useCallback(async ()=>{
    const resp = await instance.get(`?apikey=${apiKey}&s=goal&`)
    dispatch(addMovies(resp.data))
  },[dispatch]
  )

  useEffect(()=>{
    fetchMovies()
  },[fetchMovies])

  const fetchList = movieList.map(li=>{return <div>
    <img src={li.poster} alt="" />
    <h3>{li.title}</h3>
  </div>})

 return (
    <div className='movie'>
      {fetchList}
    </div>

  )
}

export default Movielisting
