import React, { useEffect } from "react";

import { fetchAsyncMoviesbyID } from "../features/movieSlice";
import { useSelector, useDispatch } from "react-redux";

const Moviedetails = () => {
  const dispatch = useDispatch();

  const id = useSelector((state) => {
    return state.movies.id;
  });

  const single = useSelector((state) => {
    return state.movies.singleMovie;
  });

  console.log(single);

  useEffect(() => {
    dispatch(fetchAsyncMoviesbyID(id));
    // eslint-disable-next-line
  }, []);

  return (
    <div className="movie-detail">
      <img className="single-poster" src={single.Poster} alt="" />
      <div>
        <div>
          <h3>Title : {single.Title}</h3>
          <h4>Awards : {single.Awards}</h4>
          <p>{single.Actors}</p>
          <p>IMDB Ratings : {single.imdbRating}</p>
        </div>
      </div>
    </div>
  );
};

export default Moviedetails;
