import React, { useEffect} from "react";
import instance from "../api/Api";
import { apiKey } from "../api/Apikey";
import { addMovies } from "../features/movieSlice";
import { useSelector, useDispatch } from "react-redux";

const Movielisting = () => {


  const dispatch = useDispatch();
  const movieList = useSelector((state) => {
    return state.movies.movies;
  });

  useEffect(() => {
    const fetchMovies = async () => {
      const resp = await instance.get(`?apikey=${apiKey}&s=policestory&`);
      dispatch(addMovies(resp.data));
    };
    fetchMovies();

    // eslint-disable-next-line
  }, []);

  const renderMovies = movieList.Response === "True" ? (
    movieList.Search.map(li=>{
      return <div className="card" key={Math.random()}>
        <img className="poster" src={li.Poster} alt="" />
        <h3>{li.Title}</h3>
    </div>})
  ) : ( <div>
      <h3>no records found</h3>
    </div>
  )
    console.log(movieList.Search);
  return <div className="movie">
    {renderMovies}
  </div>

};

export default Movielisting;
