import React from 'react'
import {Link} from 'react-router-dom'
import { searchMovies } from '../features/movieSlice'
import { useDispatch } from 'react-redux'

const Header = () => {

  const dispatch = useDispatch()

  const searchHandler = (e) =>{
    e.preventDefault()
    return dispatch(searchMovies())
  }

  return (
    <div className='main-div'>
      <Link className='link' to='/'><h3>Movie Database</h3></Link>  
      <form type='submit'>
        <input type="text" onSubmit={(e)=>{return searchHandler()}} placeholder='search movie' />
      </form>
      <h5>Api from omdbapi.com</h5>   
    </div>
  )
}

export default Header
