import React from "react";

import { fetchAsyncMoviesbyID,getSingleMovie } from "../features/movieSlice";
import { useSelector, useDispatch } from "react-redux";

const Moviedetails = () => {

  const dispatch = useDispatch()

  const id = useSelector((state) => {
    return state.movies.id
  })

  const getbyID = dispatch(fetchAsyncMoviesbyID(id))
  const singleMovie = dispatch(getSingleMovie(getbyID))
  console.log(singleMovie);

  // useEffect(() => {
